mapper.select:

select * from t_board_content 
where brd_idx = #{brd_idx} 
and grp_id = #{grp_id}

		
select * from t_board_content
where brd_idx = #{brd_idx}
and reg_password = #{reg_password}

mapper.update:
update t_board_content
set
brd_idx = #{brd_idx}

,brd_title = #{brd_title}


,brd_content = #{brd_content}

		
,parent_brd_idx = #{parent_brd_idx}


,top_flag = #{top_flag}


,brd_depth = #{brd_depth}		


,del_flag = #{del_flag}		


,secret_flag = #{secret_flag}


,reg_ip = #{reg_ip}


,reg_password = #{reg_password}


,prd_url = #{prd_url}


,brd_short = #{brd_short}


,brd_good_count = #{brd_good_count}
	

,brd_title_img_real_path = #{brd_title_img_real_path}
	
where 1 = 1 
and brd_idx = #{brd_idx}	
and grp_id = #{grp_id}

update t_board_content
set 
read_count = #{read_count} + 1
where brd_idx = #{brd_idx}	
and grp_id = #{grp_id}

update t_board_content
set 
del_flag = 'Y'
where brd_idx = #{brd_idx}	
and grp_id = #{grp_id}

mapper.delete:
delete from t_board_content 
where brd_idx = #{brd_idx}
and grp_id = #{grp_id}
